sources.stylesheets/main.scss:
body {
    font-family: 'Trebuchet MS', 'Lucida Sans Unicode', 'Lucida Grande', 'Lucida Sans', Arial, sans-serif;
}

.space {
  padding:20px;
}
$crossword-item-size: 40px;
$crossword-column-count: 23;
$crossword-row-count:20;

$color-valid-item: #9AFF67;
$color-focus-item: #FFFF74;


.crossword-board-container {
  position: relative;
  background: #FFFFFF;
  width: 100%
}

.crossword-board {
  position: absolute;
  z-index: 1;
  background: transparent;
  border: 1px solid #000000;
  width: ($crossword-item-size * $crossword-column-count);
  height: ($crossword-item-size * $crossword-row-count);
  display: grid;
  grid-template: repeat($crossword-row-count,5%) / repeat($crossword-column-count,4.55%);
  list-style-type: none;
  padding: 0;
  margin: 0 auto;
}
.crossword-board__item {
  border: 1px solid #000000;
  background: transparent;
  position: relative;
  z-index: 100;
  text-align: center;
  font-size: 20px;
  font-weight: bold;
  text-transform: uppercase;

  &:active,
  &:focus {
    background: $color-focus-item;
    border: 1px solid #000000;
    outline: 1px solid #000000;
  }
}
.crossword-board__item--blank {
  background: #000000;
  border: 1px solid #000000;
  outline: 1px solid #000000;
}

.crossword-board--labels {
  position: absolute;
  z-index: 60;
}
.crossword-board__item-label {
  position: relative;
}
.crossword-board__item-label-text {
  position: absolute;
  top: 2px;
  left: 2px;
  font-size: 14px;
  line-height: 1;
}
.crossword-board__item-label--1 {
  grid-column:1/1;
}
